import {ask} from "./openAi.js";
import {csvToText} from "./utils.js";

export const getSynonyms = async (word, count) => {
    return csvToText(await ask(`Write ${+count + 1} comma separated synonyms for "${word}":`))
}

export const getAntonyms = async (word, count) => {
    return csvToText(await ask(`Write ${+count + 1} comma separated antonyms for "${word}":`))
}