export const numberedListToText = (text) => {
    return text
        // Break by new line
        .split('\n')
        // Remove empty
        .filter(n => n)
        // Remove numbering
        .map(n => n.replace(/\d+.\s/, ''))
}

export const csvToText = (text) => {
    return text
        // Break by new line
        .split(',')
        // Remove empty
        .filter(n => n.replace(/\s/, ''))
        // Remove numbering
        .map(n => n.trim().replace(/\.$/, ''))
}