import {numberedListToText} from "./utils.js";

test("Return array from numbered list", () => {
    // Input data
    const input = '\n' +
        '\n' +
        '1. Self-directed project\n' +
        '2. Passion project\n' +
        '3. Solo project\n' +
        '4. Side project\n' +
        '5. independent project\n' +
        '6. personal undertaking\n' +
        '7. private enterprise\n' +
        '8. separate undertaking\n' +
        '9. special task\n' +
        '10. unique enterprise'

    const result = [
        'Self-directed project',
        'Passion project',
        'Solo project',
        'Side project',
        'independent project',
        'personal undertaking',
        'private enterprise',
        'separate undertaking',
        'special task',
        'unique enterprise'
    ]

    expect(numberedListToText(input)).toStrictEqual(result)
})