#!/usr/bin/env zsh
# trim and collapse whitespace - source: https://stackoverflow.com/a/40962059 query=`echo $1 | xargs`
# if query contains ':', it's full fts5 query # otherwise, change multiple groups into asterisk'd AND query # 'right arr' => 'right* AND arr*' raw=":" if test "${query#*$raw}" = "$query" then query="${query// /* AND }*" else query="${query#*$raw}" fi
/usr/bin/sqlite3 unicode.sqlite3 <<EOF ATTACH "./user.sqlite3" as user; CREATE TABLE IF NOT EXISTS user."usage" ( "id" string UNIQUE NOT NULL, "count" integer NOT NULL DEFAULT 1, PRIMARY KEY (id) );
-- the query WITH found AS ( SELECT character, name, category, hex, html, json, user.usage.count FROM main.characters('$query') LEFT JOIN user.usage ON main.characters.hex = user.usage.id ORDER BY user.usage.count DESC, rank ASC, length(name) ASC LIMIT 50 ) -- the formatter select JSON_OBJECT( 'items', JSON_GROUP_ARRAY( JSON_OBJECT( 'variables', JSON_OBJECT('hex', hex), 'title', character, 'subtitle', name || " (" || category || IIF(count > 0, ", " || count, '') || ")", 'icon', JSON_OBJECT('path', 'icon.png'), 'arg', character, 'mods', JSON_OBJECT( 'cmd', JSON_OBJECT( 'arg', html, 'subtitle', 'Copy & paste as HTML' ), 'option', JSON_OBJECT( 'arg', json, 'subtitle', 'Copy & paste as JS/Python/…' ), 'ctrl', JSON_OBJECT( 'arg', hex, 'subtitle', 'Copy & paste as Decimal value' ) ) ) ) ) FROM found; EOF

#!/usr/bin/env zsh
# Load the top 10 and display the as alfred results /usr/bin/sqlite3 user.sqlite3 <<EOF ATTACH "./unicode.sqlite3" as u; CREATE TABLE IF NOT EXISTS "usage" ( "id" string UNIQUE NOT NULL, "count" integer NOT NULL DEFAULT 1, PRIMARY KEY (id) );
-- the query WITH top AS ( SELECT id, count, hex, character, name, category, html, json FROM main.usage LEFT JOIN u.characters ON usage.id = characters.hex ORDER BY count DESC LIMIT 9 ) -- the formatter select IIF( count(*) > 0, JSON_OBJECT( 'items', JSON_GROUP_ARRAY( JSON_OBJECT( 'variables', JSON_OBJECT('hex', hex), 'title', character || " (" || count || "⨉)", 'subtitle', name || " (" || category || ")", 'icon', JSON_OBJECT('path', 'icon.png'), 'arg', character, 'mods', JSON_OBJECT( 'cmd', JSON_OBJECT( 'arg', html, 'subtitle', 'Copy & paste as HTML' ), 'option', JSON_OBJECT( 'arg', json, 'subtitle', 'Copy & paste as JS/Python/…' ), 'ctrl', JSON_OBJECT( 'arg', hex, 'subtitle', 'Copy & paste as Decimal value' ) ) ) ) ), JSON_OBJECT( 'items', JSON_ARRAY( JSON_OBJECT( 'valid', false, 'title', "UTF Search: no usage yet", 'subtitle', "Search the UTF database using 'utf <query>' first", 'icon', JSON_OBJECT('path', 'icon.png') ) ) ) ) FROM top; EOF

#!/usr/bin/env zsh

/usr/bin/sqlite3 user.sqlite3 <<EOF
    CREATE TABLE IF NOT EXISTS "usage" (
        "id" string UNIQUE NOT NULL,
        "count" integer NOT NULL DEFAULT 1,
        PRIMARY KEY (id)
    );

    INSERT INTO
        usage(id)
        VALUES('$hex')
    ON CONFLICT(id) DO
        UPDATE SET count=count+1;
EOF
