function run(argv) {
  return JSON.stringify({ alfredworkflow: { arg: argv.concat(argv) } })
}

// Helpers
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

// Main
function run(argv) {
  // Grab saved data and compare to current settings
  const data = JSON.parse(argv[0])
  const sameCollection = data["collection"] === envVar("collection")
  const sameColumns = data["columns"] === envVar("columns")

  // If settings changed, restart game
  if (!sameCollection || !sameColumns) return JSON.stringify({ alfredworkflow: { variables: { delete_state: true }}})

  // Otherwise, continue
  return JSON.stringify({ alfredworkflow: { variables: data }})
}

// Helpers
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

// Main
function run() {
  const images = envVar("images").split("\t")
  const cardBack = envVar("card_back")
  const selected = envVar("selected")
  const visible = envVar("visible").split(":").filter(e => e).map(n => parseInt(n))
  const allVisible = visible.length === parseInt(envVar("max_pairs")) * 2
  const guesses = envVar("guesses").split(":").filter(e => e).map(n => parseInt(n))
  const pickType = parseInt(envVar("moves")) % 2 === 0 ? 'Starting Pick' : 'Matching Pick'
  const moves = envVar("moves")

  const sfItems = images.map((image, index) => {
    const cardDetails = visible.concat(guesses).includes(index) ?
      { icon: image, valid: false } :
      { icon: cardBack, valid: true }

    return {
      uid: index,
      title: allVisible ? "Game Over" : pickType,
      subtitle: allVisible ? `Moves: ${moves} (Press ⌘↩ to restart)` : `Moves: ${moves}`,
      icon: { path: cardDetails["icon"] },
      valid: cardDetails["valid"],
      arg: index,
      mods: { cmd: { valid: true } }
    }
  })

  return JSON.stringify({
    variables: { delete_state: allVisible },
    skipknowledge: true,
    preselect: selected,
    items: sfItems
  })
}

// Helpers
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function setVars(object) {
  return JSON.stringify({ alfredworkflow: { variables: object }})
}

// Main
function run(argv) {
  const selected = argv[0]
  const imageNames = envVar("images").split("\t").map(image => $(image).lastPathComponent.js)
  const visible = envVar("visible").split(":").filter(e => e)
  const guesses = envVar("guesses").split(":").filter(e => e).concat(selected)

  if (guesses.length === 1) return setVars({ guesses: guesses.join(":"), selected: selected })

  if (guesses.length === 2) {
    if (imageNames[guesses[0]] === imageNames[guesses[1]]) return setVars({
      visible: visible.concat(guesses).join(":"),
      guesses: "",
      selected: selected
    })

    return setVars({ guesses: guesses.join(":"), selected: selected })
  }

  if (guesses.length === 3) return setVars({ guesses: guesses[2], selected: selected })
}

// Helpers
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

// Main
function run() {
  const visible = envVar("visible").split(":").filter(e => e).map(n => parseInt(n))
  const allVisible = visible.length === parseInt(envVar("max_pairs")) * 2

  // If all visible, game is over
  if (allVisible) return JSON.stringify({ alfredworkflow: { variables: { delete_state: true }}})

  // Otherwise, continue
  return JSON.stringify({
    visible: envVar("visible"),
    guesses: envVar("guesses"),
    moves: envVar("moves"),
    selected: envVar("selected"),
    images: envVar("images"),
    card_back: envVar("card_back"),
    max_pairs: envVar("max_pairs"),
    collection: envVar("collection"),
    columns: envVar("columns")
  })
}
