// Helpers
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function padDate(number) {
  return number.toString().padStart(2, "0")
}

function dirContents(path) {
  try {
    return $.NSFileManager.defaultManager.contentsOfDirectoryAtURLIncludingPropertiesForKeysOptionsError(
      $.NSURL.fileURLWithPath(path),
      undefined,
      $.NSDirectoryEnumerationSkipsHiddenFiles, undefined).js.map(p => p.path.js)
  } catch {
    return []
  }
}

function readMetadata(mdkey, path) {
  // Read plist
  const task = $.NSTask.alloc.init
  const stdout = $.NSPipe.pipe

  task.executableURL = $.NSURL.fileURLWithPath("/usr/bin/xattr")
  task.arguments = ["-p", `com.apple.metadata:${mdkey}`, path]
  task.standardOutput = stdout
  task.launchAndReturnError(false)

  const dataOut = stdout.fileHandleForReading.readDataToEndOfFileAndReturnError(false)
  return $.NSPropertyListSerialization.propertyListWithDataOptionsFormatError(dataOut, [], undefined, undefined).js
}

function run() {
  const imgurDir = `${envVar("alfred_workflow_data")}/imgur`

  const sfItems = dirContents(imgurDir).toSorted().toReversed().map(file => {
    const imageURL = readMetadata("kMDItemWhereFroms", file)
    const deleteURL = readMetadata("kMDItemInformation", file)

    // Recover upload date from name
    const imageDateString = $(file).stringByDeletingPathExtension.lastPathComponent.js.replace(/-.*/, "")
    const uploadDate = new Date(parseInt(imageDateString))
    const uploadYear = uploadDate.getFullYear()
    const uploadMonth = padDate(uploadDate.getMonth() + 1) // Months are zero-based
    const uploadDay = padDate(uploadDate.getDate())
    const uploadHour = padDate(uploadDate.getHours())
    const uploadMinute = padDate(uploadDate.getMinutes())
    const uploadSecond = padDate(uploadDate.getSeconds())
    const uploadParsed = `${uploadYear}/${uploadMonth}/${uploadDay} ${uploadHour}:${uploadMinute}:${uploadSecond}`

    return {
      variables: { image_url: imageURL, delete_url: deleteURL, image_icon: file },
      title: uploadParsed,
      subtitle: "↩ Preview · ⌘↩ Delete from Imgur · ⌥↩ Copy image URL",
      icon: { path: file },
      type: "file",
      arg: file
    }
  })

  return JSON.stringify({ items: sfItems })
}

// Helpers
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function writeMetadata(mdKey, path, newValue) {
  // Convert to plist
  const plistData = $.NSPropertyListSerialization.dataWithPropertyListFormatOptionsError(newValue, $.NSPropertyListXMLFormat_v1_0, 0, undefined)
  const plistString = $.NSString.alloc.initWithDataEncoding(plistData, $.NSUTF8StringEncoding).js

  // Write plist
  const task = $.NSTask.alloc.init
  task.executableURL = $.NSURL.fileURLWithPath("/usr/bin/xattr")
  task.arguments = ["-w", `com.apple.metadata:${mdKey}`, plistString, path]
  task.launchAndReturnError(false)
}

function uploadImage(imagePath, apiKey) {
  const task = $.NSTask.alloc.init
  const stdout = $.NSPipe.pipe

  task.executableURL = $.NSURL.fileURLWithPath("/usr/bin/curl")
  task.arguments = [
    "--silent",
    "--header", `Authorization: Client-ID ${apiKey}`,
    "--form", `image=@${imagePath.replaceAll('"', '\"')}`,
    "https://api.imgur.com/3/image.json"
  ]

  task.standardOutput = stdout
  task.launchAndReturnError(false)

  const dataOut = stdout.fileHandleForReading.readDataToEndOfFileAndReturnError(false)
  const stringOut = $.NSString.alloc.initWithDataEncoding(dataOut, $.NSUTF8StringEncoding).js

  return JSON.parse(stringOut)
}

// Main
function run(argv) {
  const fileManager = $.NSFileManager.defaultManager
  const apiKey = envVar("imgur_api_key")
  const imgurDir = `${envVar("alfred_workflow_data")}/imgur`

  // Ensure images folder
  fileManager.createDirectoryAtPathWithIntermediateDirectoriesAttributesError(
    imgurDir, true, undefined, undefined)

  // Upload and collect details for all images
  return argv.map(image => {
    const response = uploadImage(image, apiKey)

    // Failure
    if (!response.success) {
      console.log(JSON.stringify(response))
      throw `Failed to upload ${image}`
    }

    // Get details
    const deleteURL = `https://imgur.com/delete/${response.data.deletehash}`
    const imageURL = response.data.link

    // Name image from date
    const uid = $.NSProcessInfo.processInfo.globallyUniqueString.js.split("-")[0]
    const imageExt = $(image).pathExtension.js
    const imageIcon = `${imgurDir}/${Date.now()}-${uid}.${imageExt}`
    fileManager.copyItemAtPathToPathError(image, imageIcon, undefined)

    // Write metadata
    writeMetadata("kMDItemWhereFroms", imageIcon, imageURL)
    writeMetadata("kMDItemInformation", imageIcon, deleteURL)

    // Return link for copy
    return imageURL
  }).join("\n")
}
